import React, { useState } from 'react';
import './style-verificator.css'

function BugManagementPage() {
  const [bugs, setBugs] = useState([]);
  const [newBugTitle, setNewBugTitle] = useState('');
  const [newBugDescription, setNewBugDescription] = useState('');
  const [newBugUrgency, setNewBugUrgency] = useState('mModera');

  const addBug = () => {
    if (newBugTitle && newBugDescription) {
      const currentDate = new Date().toLocaleDateString();
      setBugs([...bugs, { title: newBugTitle, description: newBugDescription, date: currentDate, urgency: 'Moderat'}]);
      setNewBugTitle('');
      setNewBugDescription('');
    }
  };

  const deleteBug = (index) => {
    const updatedBugs = bugs.filter((_, i) => i !== index);
    setBugs(updatedBugs);
  };

  const editBug = (index) => {
    const currentBug = bugs[index];

    setNewBugTitle(currentBug.title);
    setNewBugDescription(currentBug.description);

    const updatedBugs = bugs.filter((_, i) => i!== index)
    setBugs(updatedBugs);
  }

  return (
    <div className='container'>
      <h2>Management Bug-uri</h2>
      <div>
        <h3>Adăugare Bug Nou</h3>
        <div>
          <label htmlFor="bugTitle">Bug Title:</label>
          <input
          placeholder="Titlu bug"
          value={newBugTitle}
          onChange={(e) => setNewBugTitle(e.target.value)}
        />
        </div>
        <div>
          <label htmlFor="bugDescription">Bug Description:</label>
          <input
          placeholder="Descriere bug"
          value={newBugDescription}
          onChange={(e) => setNewBugDescription(e.target.value)}
        />
        </div>
        <div>
          <label htmlFor="Bug Urgency">Bug Urgency:</label>
          <select value={newBugUrgency} onChange={(e) => setNewBugUrgency(e.target.value)}>
          <option value="Low">Low</option>
          <option value="Middle">Middle</option>
          <option value = "Urgent">Urgent</option>
        </select>
        </div>

        <button onClick={addBug}>Adăugare Bug</button>
      </div>
      <div>
        <h3>Listă Bug-uri</h3>
        <ul>
          {bugs.map((bug, index) => (
            <li key={index}>
              <div>
                <strong>{bug.title}</strong>
                <p>{bug.description}</p>
                <p><strong>Date:</strong>{bug.date}</p>
                <p><strong>Urgency:</strong>{bug.urgency}</p>
                <button onClick={() => deleteBug(index)}>Delete Bug</button>
                <button onClick={() => editBug(index)}>Edit Bug</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default BugManagementPage;
