import React, { useState } from 'react';
import './App.css';

function LoginPage({ onLogin }) {
  const [role, setRole] = useState('');
  // const [email, setEmail] = useState('');
  // const [password, setPassword] = useState('');

  const handleLogin = () => {
    if (role === '') {
      alert('Selectează un rol!');
      return;
    }
    onLogin(role);
  };

  return (
    <div className="App">
      <div>
        <h2>Pagina de Login</h2>
        <div>
          <input
            type="radio"
            id="programator"
            value="programator"
            checked={role === 'programator'}
            onChange={() => setRole('programator')}
          />
          <label htmlFor="programator">Programator</label>
        </div>
        <div>
          <input
            type="radio"
            id="verificator"
            value="verificator"
            checked={role === 'verificator'}
            onChange={() => setRole('verificator')}
          />
          <label htmlFor="verificator">Verificator</label>
        </div>
        <button onClick={handleLogin}>Autentificare</button>
      </div>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState('');
  const [userEmail, setUserEmail] = useState('');
  const [userPassword, setUserPassword] = useState('');

  const handleLogin = (role, email, password) => {
    setIsLoggedIn(true);
    setUserRole(role);
    setUserEmail(email);
    setUserPassword(password);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserRole('');
    setUserEmail('');
    setUserPassword('');
  };

  return (
    <div>
      {isLoggedIn ? (
        <div>
          <h1>Bun venit!</h1>
          <p>Rolul tău: {userRole}</p>
          <div>
          <label htmlFor="email">Adresă de Email:</label>
          <input
            type="email"
            id="email"
            value={userEmail}
            onChange={(e) => setUserEmail(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="password">Parolă:</label>
          <input
            type="password"
            id="password"
            value={userPassword}
            onChange={(e) => setUserPassword(e.target.value)}
          />
        </div>
        </div>

      ) : (
        <button onClick={handleLogin}>Login</button>
      )}
    </div>
  );
}

export default App;
