import React, { useState } from 'react';

function RoleSelectionPage({ onSelectRole }) {
  const [role, setRole] = useState('');

  const handleRoleSelection = () => {
    if (role === '') {
      alert('Selectează un rol!');
      return;
    }
    onSelectRole(role);
  };

  return (
    <div>
      <h2>Selectează Rolul</h2>
      <div>
        <input
          type="radio"
          id="programator"
          value="programator"
          checked={role === 'programator'}
          onChange={() => setRole('programator')}
        />
        <label htmlFor="programator">Programator</label>
      </div>
      <div>
        <input
          type="radio"
          id="verificator"
          value="verificator"
          checked={role === 'verificator'}
          onChange={() => setRole('verificator')}
        />
        <label htmlFor="verificator">Verificator</label>
      </div>
      <button onClick={handleRoleSelection}>Continuă</button>
    </div>
  );
}

export default RoleSelectionPage;
