import React, { useState } from 'react';
import './App.css';
import RoleSelectionPage from './roleSelection/roleSelectionPage';
import LoginPage from './login/login';
import BugManagementPage from './verificator/verificator';

function App() {
  const [selectedRole, setSelectedRole] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleRoleSelection = (role) => {
    setSelectedRole(role);
  };

  const handleLogin = (username, password) => {
    // Verificați username-ul și parola aici și faceți autentificarea
    // Setează isLoggedIn pe true dacă autentificarea este reușită
    setIsLoggedIn(true);
  };

  const handleLogout = () =>{
    setIsLoggedIn(false);
    setSelectedRole('');
  }

  // return (
  //   <div className="App">
  //     {!selectedRole ? (
  //       <RoleSelectionPage onSelectRole={handleRoleSelection} />
  //     ) : (
  //       <LoginPage onLogin={handleLogin} />

  //     )}
  //     <button onClick={handleLogout}>Logout</button>
  //   </div>
  // );
  return (
    <div className="App">
      {!isLoggedIn ? (
        !selectedRole ? (
          <RoleSelectionPage onSelectRole={handleRoleSelection} />
        ) : (
          <LoginPage onLogin={handleLogin} />
        )
      ) : (
        selectedRole === 'verificator' ? (
          <BugManagementPage />
        ) : (
          <h1>Bun venit!</h1>
        )
      )}
      {isLoggedIn && <button onClick={handleLogout}>Logout</button>}
    </div>
  );
}



export default App;
