import React, { useState } from 'react';
import './App.css';

function LoginPage({ onLogin }) {
  const [role, setRole] = useState('');

  const handleLogin = () => {
    if (role === '') {
      alert('Selectează un rol!');
      return;
    }
    onLogin(role);
  };

  return (
    <div className="App">
      <div>
        <h2>Pagina de Login</h2>
        <div>
          <input
            type="radio"
            id="programator"
            value="programator"
            checked={role === 'programator'}
            onChange={() => setRole('programator')}
          />
          <label htmlFor="programator">Programator</label>
        </div>
        <div>
          <input
            type="radio"
            id="verificator"
            value="verificator"
            checked={role === 'verificator'}
            onChange={() => setRole('verificator')}
          />
          <label htmlFor="verificator">Verificator</label>
        </div>
        <button onClick={handleLogin}>Autentificare</button>
      </div>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState('');

  const handleLogin = (role) => {
    setIsLoggedIn(true);
    setUserRole(role);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserRole('');
  };

  return (
    <div>
      {isLoggedIn ? (
        <div>
          <h1>Bun venit!</h1>
          <p>Rolul tău: {userRole}</p>
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <LoginPage onLogin={handleLogin} />
      )}
    </div>
  );
}

export default App;
