import React, { useState } from 'react';
import './App.css';
import RoleSelectionPage from './roleSelectionPage';
import LoginPage from './login';
import BugManagementPage from './verificator';
import ProgrammerPage from './programator';

function App() {
  const [selectedRole, setSelectedRole] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [bugs, setBugs] = useState([]);

  const handleRoleSelection = (role) => {
    setSelectedRole(role);
  };

  const handleLogin = (username, password) => {

    setIsLoggedIn(true);
    setBugs([
      { id: 1, title: 'Bug 1', description: 'Descriere bug 1', date:'12.02.2024', urgency:'Normal'},
      { id: 2, title: 'Bug 2', description: 'Descriere bug 2', date:'13.02.2024', urgency:'Critical' },
      // Adăugați aici mai multe bug-uri dacă este necesar
    ]);
  };
  const handleDeleteBug = (bugId) => {
    // Elimină bug-ul din lista de bug-uri
    const updatedBugs = bugs.filter((bug) => bug.id !== bugId);
    setBugs(updatedBugs);
    // Aici puteți adăuga logica pentru a actualiza și lista de bug-uri pe server, dacă este necesar
  };

  const handleLogout = () =>{
    setIsLoggedIn(false);
    setSelectedRole('');
  }

  return (
    <div className="App">
      {isLoggedIn && <button className='logout-button' onClick={handleLogout}>Logout</button>}
      {!isLoggedIn ? (
        !selectedRole ? (
          <RoleSelectionPage onSelectRole={handleRoleSelection} />
        ) : (
          <LoginPage onLogin={handleLogin} />
        )
      ) : (
        selectedRole === 'verificator' ? (
          <BugManagementPage />
        ) : (
          <ProgrammerPage bugs={bugs} onDeleteBug={handleDeleteBug} />
        )
      )}

    </div>
  );
}



export default App;
