import React, { useState } from 'react';
import './style.css'

function RoleSelectionPage({ onSelectRole }) {
  const [role, setRole] = useState('');

  const handleRoleSelection = () => {
    if (role === '') {
      alert('Selectează un rol!');
      return;
    }
    onSelectRole(role);
  };

  return (
    <div className='container-role'>
      <h2>Choose your role:</h2>
      <div>
        <input
          type="radio"
          id="programator"
          value="programator"
          checked={role === 'programator'}
          onChange={() => setRole('programator')}
        />
        <label htmlFor="programator">Programmer</label>
      </div>
      <div>
        <input
          type="radio"
          id="verificator"
          value="verificator"
          checked={role === 'verificator'}
          onChange={() => setRole('verificator')}
        />
        <label htmlFor="verificator">Verifier</label>
      </div>
      <button className="button" onClick={handleRoleSelection}>Ok</button>
    </div>
  );
}

export default RoleSelectionPage;
